import React, { useState } from 'react'
import {
  TrashIcon,
  PencilIcon,
  AppContainer,
  Heading,
  SearchContainer,
  Container,
  InputLine,
  ContentBox,
  SearchBar,
  Table,
  ClearAllContainer,
  ClearAll,
  Regular,
  EmptyPrompt,
  EmptySpace,
} from './elements'
import ListItem from './ListItem'

const BugList = () => {
  const [items, setItems] = useState([
    { id: 1, done: true, text: 'Get up early' },
    { id: 2, done: true, text: 'Do some meditation' },
    { id: 3, done: false, text: 'Eat a healthy breakfast' },
    { id: 4, done: false, text: 'Drink plenty of water' },
    { id: 5, done: false, text: 'Exercise' },
    { id: 6, done: false, text: 'Have fun' },
  ])
  const [text, setText] = useState('')
  const trashHandler = (id) => {
    const newItems = [...items]
    const itemIndex = newItems.findIndex((item) => item.id === id)
    newItems.splice(itemIndex, 1)
    setItems(newItems)
  }
  const trashAllHandler = () => setItems([])

  const tickHandler = (id) => {
    const newItems = [...items]
    const itemIndex = newItems.findIndex((item) => item.id === id)
    newItems[itemIndex].done = !newItems[itemIndex].done
    setItems(newItems)
  }
  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && e.target.value) {
      inputHandler(e)
    }
  }
  const inputHandler = (e) => {
    const newItem = {
      id: items.length + 1,
      done: false,
      text: e.target.value,
    }
    const newItems = [...items, newItem]
    setItems(newItems)
    setText('')
  }

  return (
    <AppContainer>
      <Heading>Reports</Heading>
      <SearchContainer>
        <Container>
          {items.length === 0 && (
            <EmptyPrompt>
              <Regular>Add some things to your to do list!</Regular>
            </EmptyPrompt>
          )}
          <Table>
            <tbody>
              {items.map((item) => (
                <ListItem
                  item={item}
                  tickHandler={tickHandler}
                  trashHandler={trashHandler}
                />
              ))}
            </tbody>
          </Table>
        </Container>
      </SearchContainer>
      {items.length ? (
        <ClearAllContainer>
          <ClearAll onClick={trashAllHandler}>
            <TrashIcon />
            Clear all
          </ClearAll>
        </ClearAllContainer>
      ) : (
        ''
      )}
    </AppContainer>
  )
}

export default BugList
