import React from 'react'
import logo from './logo.svg'
// import trash from './trash.svg'
import './App.css'
import styled from 'styled-components'

const ListContainer = styled.div`
  width: 650px;
  height: 800px;
  background: #e1e6eb;
`
const Header = styled.h1`
  font-size: 1.5em;
  color: #354352;
  padding-top: 50px;
`
const SearchContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`

const Container = styled.div`
  width: 530px;
  min-height: 60px;
  border-radius: 5px;
  background: beige;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  justify-content: center;
  box-sizing: border-box;
`
const Line = styled.div`
  background: lightgray;
  // padding: 15px;
  padding: 10px 20px;
  height: 60px;
  box-sizing: border-box;
  display: flex;
  // justify-content: space-between;
  align-items: center;
`
const InputLine = styled(Line)`
  background: gray;
  // padding: 15px;
  padding: 10px 20px;

  box-sizing: border-box;
  display: flex;
  // justify-content: space-between;
  // align-items: center;
`
const ContentBox = styled.div`
  height: 30px;
  font-size: 0.7em;
  color: #5eb77f;
  background: green;
  width: 400px;
  display: flex;
  align-items: center;
  margin-right: 30px;
`

const Icon = styled.div`
  width: 20px;
  height: 20px;
  background-size: contain;
  background-position: center center;
  background-repeat: no-repeat;
  // background: black;
  // margin: 10px 10px;
  &:hover {
    pointer: 
  }
`
const LeftIcon = styled(Icon)`
  margin-right: 20px;
`
const PencilIcon = styled(LeftIcon)`
  background-image: url('./pencil.svg');
`
const DoneIcon = styled(LeftIcon)`
  background-image: url('./done.svg');
`
const UndoneIcon = styled(LeftIcon)`
  background-image: url('./undone.svg');
`
const TrashIcon = styled(Icon)`
  background-image: url('./trash.svg');
  width: 15px;
  height: 15px;
`
const TextContainer = styled.div``

const SearchBarContainer = styled.div`
  width: 530px;
  height: 60px;
  background: white;
  border-radius: 5px;
  // padding: 0 10px;
  display: flex;
  align-items: center;
`

const SearchBar = styled.input`
  width: 400px;
  height: 20px;
  background: pink;
  // background: yellow;
  // border-radius: 5px;
  padding: 10px 20px;
`

function App() {
  const results = [
    { id: 1, done: true, text: 'Get up early' },
    // { id: 2, done: false, text: 'Do some meditation' },
    // { id: 3, done: true, text: 'eat a healthy breakfast' },
  ]

  const listItem = ({ done, text }) => (
    <Line>
      {done ? <DoneIcon /> : <UndoneIcon />}
      <ContentBox>
        <TextContainer>{text}</TextContainer>
      </ContentBox>
      <TrashIcon />
    </Line>
  )
  return (
    <div className="App">
      <header className="App-header">
        <ListContainer>
          <Header>REACT TO-DO</Header>
          <SearchContainer>
            <Container>
              <InputLine>
                <PencilIcon />
                <ContentBox>
                  <TextContainer></TextContainer>
                </ContentBox>
              </InputLine>
            </Container>
            <SearchBarContainer>
              <Icon />
              <SearchBar placeholder="What needs to be accomplished?" /> <br />
            </SearchBarContainer>
            <br />
            <Container>{results.map((item) => listItem(item))}</Container>
          </SearchContainer>
        </ListContainer>
      </header>
    </div>
  )
}

export default App
