import React from 'react'
import logo from './logo.svg'
import './App.css'
import styled from 'styled-components'

const ListContainer = styled.div`
  width: 650px;
  height: 800px;
  background: #e1e6eb;
`
const Header = styled.h1`
  font-size: 1.5em;
  color: #354352;
  padding-top: 50px;
`
const SearchContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`
const SearchBarContainer = styled.div`
  width: 540px;
  height: 60px;
  background: white;
  background: yellow;
  border-radius: 5px;
  padding: 10px;
`
const SearchBar = styled.input`
  width: 510px;
  height: 60px;
  background: white;
  background: yellow;
  border-radius: 5px;
  padding: 10px;
`
const InputIcon = styled.i`
  padding: 10px;
`
const ListResults = styled.div`
  width 540px;
  min-height: 60px;
  background: white;
  background: orange;
  border-radius: 5px;

`

function App() {
  const results = ['Get up early', 'Do some meditation']
  return (
    <div className="App">
      <header className="App-header">
        <ListContainer>
          <Header>REACT TO-DO</Header>
          <SearchContainer>
            <InputIcon />
            <SearchBarContainer></SearchBarContainer>
            <SearchBar placeholder="What needs to be accomplished?" /> <br />
            <ListResults></ListResults>
          </SearchContainer>
        </ListContainer>
      </header>
    </div>
  )
}

export default App
