import React, { useState } from 'react'
import {
  TrashIcon,
  AppContainer,
  Heading,
  SearchContainer,
  Container,
  Table,
  ClearAllContainer,
  ClearAll,
  Regular,
  EmptyPrompt,
} from './elements'
import ListItem from './ListItem'

const BugList = () => {
  const [items, setItems] = useState([
    { id: 1, state: false, type: 'Spam', text: 'Get up early' },
    { id: 2, state: false, type: 'Spam', text: 'Do some meditation' },
    { id: 3, state: false, type: 'Spam', text: 'Eat a healthy breakfast' },
    { id: 4, state: false, type: 'Spam', text: 'Drink plenty of water' },
  ])

  const blockHandler = (id) => {
    const newItems = [...items]
    const itemIndex = newItems.findIndex((item) => item.id === id)
    newItems.splice(itemIndex, 1)
    setItems(newItems)
  }

  const trashAllHandler = () => setItems([])

  const resolveHandler = (id) => {
    const newItems = [...items]
    const itemIndex = newItems.findIndex((item) => item.id === id)
    newItems[itemIndex].state = !newItems[itemIndex].state
    setItems(newItems)
  }

  return (
    <AppContainer>
      <Heading>Reports</Heading>
      <SearchContainer>
        <Container>
          {items.length === 0 && (
            <EmptyPrompt>
              <Regular>No bugs!</Regular>
            </EmptyPrompt>
          )}
          <Table>
            <tbody>
              {items.map((item) => (
                <ListItem
                  key={item.id}
                  item={item}
                  resolveHandler={resolveHandler}
                  blockHandler={blockHandler}
                />
              ))}
            </tbody>
          </Table>
        </Container>
      </SearchContainer>
      {items.length ? (
        <ClearAllContainer>
          <ClearAll onClick={trashAllHandler}>
            <TrashIcon />
            Clear all
          </ClearAll>
        </ClearAllContainer>
      ) : (
        ''
      )}
    </AppContainer>
  )
}

export default BugList
