import React, { useState } from 'react'
import {
  TrashIcon,
  AppContainer,
  Heading,
  SearchContainer,
  Container,
  Table,
  ClearAllContainer,
  ClearAll,
  Regular,
  EmptyPrompt,
} from './elements'
import ListItem from './ListItem'

const BugList = () => {
  const [items, setItems] = useState([
    { id: 1, state: 'Open', type: 'Spam', text: 'Get up early' },
    { id: 2, state: 'Open', type: 'Spam', text: 'Do some meditation' },
    { id: 3, state: 'Open', type: 'Spam', text: 'Eat a healthy breakfast' },
    { id: 4, state: 'Open', type: 'Spam', text: 'Drink plenty of water' },
  ])
  const [text, setText] = useState('')
  const blockHandler = (id) => {
    const newItems = [...items]
    const itemIndex = newItems.findIndex((item) => item.id === id)
    newItems.splice(itemIndex, 1)
    setItems(newItems)
  }
  const trashAllHandler = () => setItems([])

  const resolveHandler = (id) => {
    const newItems = [...items]
    const itemIndex = newItems.findIndex((item) => item.id === id)
    newItems[itemIndex].done = !newItems[itemIndex].done
    setItems(newItems)
  }
  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && e.target.value) {
      inputHandler(e)
    }
  }
  const inputHandler = (e) => {
    const newItem = {
      id: items.length + 1,
      done: false,
      text: e.target.value,
    }
    const newItems = [...items, newItem]
    setItems(newItems)
    setText('')
  }

  return (
    <AppContainer>
      <Heading>Reports</Heading>
      <SearchContainer>
        <Container>
          {items.length === 0 && (
            <EmptyPrompt>
              <Regular>Add some things to your to do list!</Regular>
            </EmptyPrompt>
          )}
          <Table>
            <tbody>
              {items.map((item) => (
                <ListItem
                  key={item.id}
                  item={item}
                  Handler={Handler}
                  blockHandler={blockHandler}
                />
              ))}
            </tbody>
          </Table>
        </Container>
      </SearchContainer>
      {items.length ? (
        <ClearAllContainer>
          <ClearAll onClick={trashAllHandler}>
            <TrashIcon />
            Clear all
          </ClearAll>
        </ClearAllContainer>
      ) : (
        ''
      )}
    </AppContainer>
  )
}

export default BugList
