import React from 'react'
import logo from './logo.svg'
import './App.css'
import styled from 'styled-components'

const ListContainer = styled.div`
  width: 650px;
  height: 800px;
  background: #e1e6eb;
`
const Header = styled.h1`
  font-size: 1.5em;
  color: #354352;
  padding-top: 50px;
`
const SearchContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`
const SearchBarContainer = styled.div`
  width: 530px;
  height: 60px;
  background: white;
  border-radius: 5px;
  // padding: 10px;
  display: flex;
  align-items: center;
`
const ResultsContainer = styled.div`
  width: 530px;
  height: 60px;
  background: white;
  border-radius: 5px;
  // padding: 10px;
  display: flex;
  align-items: center;
`
const SearchBar = styled.input`
  width: 400px;
  height: 20px;
  background: pink;
  // background: yellow;
  // border-radius: 5px;
  padding: 10px;
`
const InputIcon = styled.div`
  width: 40px;
  height: 40px;
  background: black;
  margin: 10px 20px;
`
const ListResults = styled.div`
  width 530px;
  min-height: 60px;
  background: white;
  background: orange;
  border-radius: 5px;
padding: 10px;
`

const ListItemContainer = styled.div`
width: 530px;
height: 60px;
background: white;
border-radius: 5px;
// padding: 10px;
display: flex;
align-items: center;
`
const ListItem = styled.div`
  font-size: 0.7em;
  color: #5eb77f;
`

function App() {
  const results = ['Get up early', 'Do some meditation']
  return (
    <div className="App">
      <header className="App-header">
        <ListContainer>
          <Header>REACT TO-DO</Header>
          <SearchContainer>
            <SearchBarContainer>
              <InputIcon />
              <SearchBar placeholder="What needs to be accomplished?" /> <br />
            </SearchBarContainer>
            <ListResults>
              {results.map((item) => (
                <SearchBarContainer>
                  <InputIcon />
                  <ListItem>{item}</ListItem>
                </SearchBarContainer>
              ))}
            </ListResults>
          </SearchContainer>
        </ListContainer>
      </header>
    </div>
  )
}

export default App
