import React from 'react'
import logo from './logo.svg'
import './App.css'
import styled from 'styled-components'

const ListContainer = styled.div`
  width: 650px;
  height: 800px;
  background: #e1e6eb;
`
const Header = styled.h1`
  font-size: 1.5em;
  color: #354352;
  padding-top: 50px;
`
const SearchContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`
const Container = styled.div`
  width: 530px;
  min-height: 60px;
  border-radius: 5px;
  background: red;
  // padding: 15px;
  padding: 10px 20px;

  box-sizing: border-box;
  display: flex;
  justify-content: space-between;
`
const ContentBox = styled.div`
  height: 30px;
  font-size: 0.7em;
  color: #5eb77f;
  background: green;
  width: 90%;
`

const SearchBarContainer = styled.div`
  width: 530px;
  height: 60px;
  background: white;
  border-radius: 5px;
  // padding: 0 10px;
  display: flex;
  align-items: center;
`

const SearchBar = styled.input`
  width: 400px;
  height: 20px;
  background: pink;
  // background: yellow;
  // border-radius: 5px;
  padding: 10px 20px;
`
const InputIcon = styled.div`
  width: 30px;
  height: 30px;
  background: black;
  // margin: 10px 10px;
`
const TrashIcon = styled.div``

const ListResults = styled.div`
  width 530px;
  min-height: 60px;
  background: white;
  background: orange;
  border-radius: 5px;
  // padding: 10px;
  display: flex;
  flex-direction: column;
  align-items: center;
  overflow: hidden;
`

const ListItemContainer = styled.div`
  width: 530px;
  height: 60px;
  background: white;
  display: flex;
  align-items: center;
  background: yellow;
`

function App() {
  const results = [
    'Get up early',
    'Do some meditation',
    'eat a healthy breakfast',
  ]
  return (
    <div className="App">
      <header className="App-header">
        <ListContainer>
          <Header>REACT TO-DO</Header>
          <SearchContainer>
            <Container>
              <ContentBox>icon left icon right</ContentBox>
              <InputIcon></InputIcon>
            </Container>
            <SearchBarContainer>
              <InputIcon />
              <SearchBar placeholder="What needs to be accomplished?" /> <br />
            </SearchBarContainer>
            <br />
            <ListResults>
              {results.map((item) => (
                <ListItemContainer>
                  <InputIcon />
                  {/* <ListItem>{item}</ListItem> */}
                  <TrashIcon />
                </ListItemContainer>
              ))}
            </ListResults>
          </SearchContainer>
        </ListContainer>
      </header>
    </div>
  )
}

export default App
